import { useEffect, useState } from "react";
import { apiGet, apiPut } from "../api";

export default function Profile() {
  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");
  const [email, setEmail] = useState("");
  const [avatar, setAvatar] = useState("");
  const [password, setPassword] = useState("");
  const [msg, setMsg] = useState("");

  // Load profile data on component load
  useEffect(() => {
    loadProfile();
  }, []);

  const loadProfile = async () => {
    const res = await apiGet("/profile");
    if (res.user) {
      setName(res.user.name);
      setPhone(res.user.phone);
      setEmail(res.user.email);
      setAvatar(res.user.avatar);
    }
  };

  const updateProfile = async (e) => {
    e.preventDefault();

    const body = {
      name,
      phone,
      avatar,
      ...(password ? { password } : {})
    };

    const res = await apiPut("/profile/update", body);
    setMsg(res.message || "Updated successfully!");
  };

  return (
    <div className="flex justify-center items-center min-h-screen bg-gray-100 px-3">
      <div className="bg-white shadow p-6 rounded w-96">

        <h2 className="text-xl font-bold text-center mb-4">Profile</h2>

        {/* Avatar Preview */}
        <div className="flex justify-center mb-4">
          <img
            src={avatar || "https://i.ibb.co/2FsfC0F/user.png"}
            className="w-24 h-24 rounded-full border object-cover"
          />
        </div>

        <form onSubmit={updateProfile}>

          {/* Name */}
          <input
            className="w-full p-2 border rounded mb-3"
            value={name}
            placeholder="Name"
            onChange={(e) => setName(e.target.value)}
          />

          {/* Phone */}
          <input
            className="w-full p-2 border rounded mb-3"
            value={phone}
            placeholder="Phone"
            onChange={(e) => setPhone(e.target.value)}
          />

          {/* Email (read-only) */}
          <input
            className="w-full p-2 border rounded mb-3 bg-gray-200"
            value={email}
            readOnly
          />

          {/* Avatar URL */}
          <input
            className="w-full p-2 border rounded mb-3"
            value={avatar}
            placeholder="Profile Picture URL"
            onChange={(e) => setAvatar(e.target.value)}
          />

          {/* Change Password */}
          <input
            type="password"
            className="w-full p-2 border rounded mb-3"
            placeholder="New Password (optional)"
            onChange={(e) => setPassword(e.target.value)}
          />

          <button className="w-full bg-blue-600 text-white p-2 rounded">
            Update Profile
          </button>

          <p className="mt-3 text-green-600">{msg}</p>
        </form>

      </div>
    </div>
  );
}
