import { useState, useEffect } from "react";
import Navbar from "../components/Navbar";
import { apiGet, apiPost, apiPut, apiDelete } from "../api";

export default function Tasks() {
  const token = localStorage.getItem("token");

  const [tasks, setTasks] = useState([]);
  const [title, setTitle] = useState("");
  const [desc, setDesc] = useState("");

  async function loadTasks() {
    const data = await apiGet("/tasks", token);
    setTasks(data);
  }

  const addTask = async e => {
    e.preventDefault();
    await apiPost("/tasks", { title, description: desc }, token);
    setTitle("");
    setDesc("");
    loadTasks();
  };

  const toggleComplete = async (task) => {
    await apiPut(`/tasks/${task._id}`, { ...task, completed: !task.completed }, token);
    loadTasks();
  };

  const deleteTask = async (id) => {
    await apiDelete(`/tasks/${id}`, token);
    loadTasks();
  };

  useEffect(() => {
    loadTasks();
  }, []);

  return (
    <div>
      <Navbar />

      <div className="p-6 max-w-xl mx-auto">

        <h2 className="text-3xl font-bold mb-4">Your Tasks</h2>

        {/* Add Task */}
        <form onSubmit={addTask} className="mb-6">
          <input
            className="w-full p-2 border rounded mb-2"
            placeholder="Task title"
            value={title}
            onChange={e => setTitle(e.target.value)}
            required
          />
          <input
            className="w-full p-2 border rounded mb-2"
            placeholder="Task description"
            value={desc}
            onChange={e => setDesc(e.target.value)}
          />
          <button className="bg-green-600 text-white px-4 py-2 rounded">
            Add Task
          </button>
        </form>

        {/* List Tasks */}
        <div className="space-y-3">
          {tasks.map(task => (
            <div key={task._id} className="p-3 border rounded flex justify-between">
              <div>
                <h3 className={`font-bold ${task.completed ? "line-through" : ""}`}>
                  {task.title}
                </h3>
                <p className="text-gray-600">{task.description}</p>
              </div>

              <div className="space-x-2">
                <button 
                  onClick={() => toggleComplete(task)}
                  className="px-3 py-1 bg-blue-500 text-white rounded"
                >
                  {task.completed ? "Undo" : "Done"}
                </button>

                <button 
                  onClick={() => deleteTask(task._id)}
                  className="px-3 py-1 bg-red-500 text-white rounded"
                >
                  Delete
                </button>
              </div>

            </div>
          ))}
        </div>

      </div>
    </div>
  );
}
